#!/bin/bash
# Smoke Tests for Cats-Dogs Classifier API
# Tests basic functionality after deployment

set -e  # Exit on error

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

# Configuration
API_URL="${API_URL:-http://localhost:8000}"
TIMEOUT=10
MAX_RETRIES=5
RETRY_DELAY=2

echo "========================================="
echo "Cats-Dogs Classifier API - Smoke Tests"
echo "========================================="
echo "API URL: $API_URL"
echo ""

# Function to print colored output
print_success() {
    echo -e "${GREEN}✓ $1${NC}"
}

print_error() {
    echo -e "${RED}✗ $1${NC}"
}

print_info() {
    echo -e "${YELLOW}ℹ $1${NC}"
}

# Function to wait for API to be ready
wait_for_api() {
    print_info "Waiting for API to be ready..."
    
    for i in $(seq 1 $MAX_RETRIES); do
        if curl -s -f -m $TIMEOUT "$API_URL/health" > /dev/null 2>&1; then
            print_success "API is ready!"
            return 0
        fi
        
        if [ $i -lt $MAX_RETRIES ]; then
            print_info "Attempt $i/$MAX_RETRIES failed, retrying in ${RETRY_DELAY}s..."
            sleep $RETRY_DELAY
        fi
    done
    
    print_error "API failed to become ready after $MAX_RETRIES attempts"
    return 1
}

# Test 1: Health Check
test_health_check() {
    echo ""
    echo "Test 1: Health Check"
    echo "-------------------"
    
    response=$(curl -s -w "\n%{http_code}" -m $TIMEOUT "$API_URL/health")
    http_code=$(echo "$response" | tail -n1)
    body=$(echo "$response" | head -n-1)
    
    if [ "$http_code" -eq 200 ]; then
        print_success "Health check passed (HTTP $http_code)"
        echo "Response: $body"
        
        # Check if model is loaded
        if echo "$body" | grep -q '"model_loaded":true'; then
            print_success "Model is loaded"
        else
            print_error "Model is not loaded"
            return 1
        fi
    else
        print_error "Health check failed (HTTP $http_code)"
        echo "Response: $body"
        return 1
    fi
}

# Test 2: Root Endpoint
test_root_endpoint() {
    echo ""
    echo "Test 2: Root Endpoint"
    echo "-------------------"
    
    response=$(curl -s -w "\n%{http_code}" -m $TIMEOUT "$API_URL/")
    http_code=$(echo "$response" | tail -n1)
    body=$(echo "$response" | head -n-1)
    
    if [ "$http_code" -eq 200 ]; then
        print_success "Root endpoint accessible (HTTP $http_code)"
        echo "Response: $body"
    else
        print_error "Root endpoint failed (HTTP $http_code)"
        return 1
    fi
}

