# Test 4: Metrics Endpoint
test_metrics() {
    echo ""
    echo "Test 4: Metrics Endpoint"
    echo "----------------------"
    
    response=$(curl -s -w "\n%{http_code}" -m $TIMEOUT "$API_URL/metrics")
    http_code=$(echo "$response" | tail -n1)
    
    if [ "$http_code" -eq 200 ]; then
        print_success "Metrics endpoint accessible (HTTP $http_code)"
    else
        print_error "Metrics endpoint failed (HTTP $http_code)"
        return 1
    fi
}

# Test 5: Invalid Request Handling
test_invalid_request() {
    echo ""
    echo "Test 5: Invalid Request Handling"
    echo "--------------------------------"
    
    # Send invalid image shape
    invalid_image='{"image": [[1, 2, 3]]}'
    
    response=$(curl -s -w "\n%{http_code}" -m $TIMEOUT -X POST \
        -H "Content-Type: application/json" \
        -d "$invalid_image" \
        "$API_URL/predict")
    
    http_code=$(echo "$response" | tail -n1)
    
    if [ "$http_code" -eq 422 ] || [ "$http_code" -eq 400 ]; then
        print_success "Invalid request correctly rejected (HTTP $http_code)"
    else
        print_error "Invalid request handling failed (HTTP $http_code, expected 422 or 400)"
        return 1
    fi
}

# Main execution
main() {
    # Wait for API to be ready
    if ! wait_for_api; then
        print_error "Smoke tests failed: API not ready"
        exit 1
    fi
    
    # Run all tests
    tests_passed=0
    tests_failed=0
    
    if test_health_check; then
        ((tests_passed++))
    else
        ((tests_failed++))
    fi
    
    if test_root_endpoint; then
        ((tests_passed++))
    else
        ((tests_failed++))
    fi
    
    if test_prediction_valid; then
        ((tests_passed++))
    else
        ((tests_failed++))
    fi
    
    if test_metrics; then
        ((tests_passed++))
    else
        ((tests_failed++))
    fi
    
    if test_invalid_request; then
        ((tests_passed++))
    else
        ((tests_failed++))
    fi
    
    # Summary
    echo ""
    echo "========================================="
    echo "Smoke Tests Summary"
    echo "========================================="
    echo "Tests passed: $tests_passed"
    echo "Tests failed: $tests_failed"
    echo ""
    
    if [ $tests_failed -eq 0 ]; then
        print_success "All smoke tests passed!"
        exit 0
    else
        print_error "Some smoke tests failed!"
        exit 1
    fi
}

# Run main function
main
